import React, { useState,useEffect } from 'react';
import logo from "../images/logo-white-2.png";
import { useNavigate } from 'react-router-dom';



const SignUpEmp = () => {
    
  const [loginObj, setLoginObj] = useState({name: '', emailId: '', password: ''});
  const [hidePassword, setHidePassword] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate(); 
  const [isSuccessPopupVisible, setIsSuccessPopupVisible] = useState(false);
  const [categories, setCategories] = useState([]); 
  const [selectedCategory, setSelectedCategory] = useState(""); 
  const [loading, setLoading] = useState(true); 

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLoginObj({ ...loginObj, [name]: value });
  };

  const handleSignUp = async (e) => {
    e.preventDefault(); 
    try {
      const response = await fetch('http://10.10.20.9:7071/IIA-Website-1.0.0/registration/saveRegistration', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },

        
        body: JSON.stringify({
          organisation: loginObj.organisation || "", 
          name: loginObj.name,
          phoneNumber: loginObj.phoneNumber,
          email: loginObj.emailId,
          password: loginObj.password,
          categories: selectedCategory, 
        }),
      });

      if (!response.ok) {
        const errorMessage = await response.text();
        throw new Error(errorMessage || 'Failed to register. Please try again.');
      }

      const contentType = response.headers.get('Content-Type');
      if (contentType && contentType.includes('application/json')) {
        const data = await response.json();
        console.log('Registration successful:', data);
      
        alert('Registration successful:');
        // Redirect to login page after successful registration
        navigate('/login');
      } else {
        const textResponse = await response.text();
        console.log('Response:', textResponse);
        throw new Error(textResponse);
      }
    } catch (error) {
      console.error('Error:', error);
      setError(error.message || 'Something went wrong, please try again.');
    }
  };

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('http://10.10.20.9:7071/IIA-Website-1.0.0/enum/categoryies');
        const data = await response.json();
        setCategories(data); // Since API returns an array of strings, directly set it
        setLoading(false);
      } catch (error) {
        console.error("Error fetching categories:", error);
        setLoading(false);
      }
    };

    fetchCategories(); // Call the function to fetch data when the component mounts
  }, []);

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const handleBack = () => {
    
    navigate('/login')
  };
  
  
  

  return (
    <div className="login-body1">
      <div className="login-container1">
        {/* Login Form Section */}
        <div className="form-section1">
          <div className="form-content1">
          <div className="form-header">
  <button className="back-btn" onClick={handleBack}>
    <i className="fa fa-arrow-left" aria-hidden="true"></i> {/* Back icon */}
   
  </button>
  <img src={logo} alt="ACTUS Logo" className="logo1" />
</div>

            <h5>Sign Up</h5>
            <div className="form-group">
      {loading ? (
        <p>Loading categories...</p>
      ) : (
        <select
          name="options"
          required
          className="custom-dropdown1"
          value={selectedCategory}
          onChange={handleCategoryChange}
        >
          <option value="" disabled>
            Category
          </option>
          {/* Dynamically render options from API data */}
          {categories.map((category, index) => (
            <option key={index} value={category}>
              {category}
            </option>
          ))}
        </select>
      )}
    </div>
            <form onSubmit={handleSignUp}>
            {/* <label>Name </label> */}
            <div className="form-group1">
                {/* <label>Email ID <span style={{ color: 'red' }}>*</span></label> */}
                <input
                  type="text"
                  name="organisation"
                  value={loginObj.organisation}
                  onChange={handleChange}
                  required
                  placeholder="Organization *"
                  className="form-control"
                />
              </div>
            <div className="form-group1">
                {/* <label>Email ID <span style={{ color: 'red' }}>*</span></label> */}
                <input
                  type="text"
                  name="name"
                  value={loginObj.name}
                  onChange={handleChange}
                  required
                  placeholder="Enter Name *"
                  className="form-control"
                />
              </div>
              {/* <label>Email ID</label> */}
              <div className="form-group1">
                {/* <label>Email ID <span style={{ color: 'red' }}>*</span></label> */}
                <input
                  type="email"
                  name="emailId"
                  value={loginObj.emailId}
                  onChange={handleChange}
                  required
                  placeholder="Enter Email ID *"
                  className="form-control"
                />
              </div>

              <div className="form-group1">
                {/* <label>Email ID <span style={{ color: 'red' }}>*</span></label> */}
                <input
                  type="MobileNo"
                  name="MobileNo"
                  value={loginObj.MobileNo}
                  onChange={handleChange}
                  required
                  placeholder="Mobile No *"
                  className="form-control"
                />
              </div>
              

              
              <div className="form-group1 password-field">
  {/* <label>Password </label> */}
  <div className="input-container1">
    <input
      type={hidePassword ? 'password' : 'text'}
      name="password"
      value={loginObj.password}
      onChange={handleChange}
      required
      placeholder="Enter Password *"
      className="form-control"
    />
    <button 
      type="button" 
      className="toggle-password" 
      onClick={() => setHidePassword(prev => !prev)}
    >
      {hidePassword ? (
        <i className="fa fa-eye-slash" aria-hidden="true"></i> // Eye slash icon for hidden password
      ) : (
        <i className="fa fa-eye" aria-hidden="true"></i> // Eye icon for visible password
      )}
    </button>
  </div>
</div>

              <div className="form-footer1">
            
                <button type="submit" className="btn1 btn1-primary">
                  Signup
                </button>
              </div>
            </form>
            
            {error && <span className="error-text">{error}</span>}
          </div>
        </div>
      </div>
      
    </div>
  );
};

export default SignUpEmp ;
