import React, { useState,useEffect } from 'react';
import logo from "../images/logo-white-2.png";
import { useNavigate } from 'react-router-dom';



const SignUpEmp = () => {
    
  const [loginObj, setLoginObj] = useState({name: '', emailId: '', password: ''});
  const [hidePassword, setHidePassword] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate(); 
  const [isSuccessPopupVisible, setIsSuccessPopupVisible] = useState(false);
  const [categories, setCategories] = useState([]); // State to store fetched categories
  const [selectedCategory, setSelectedCategory] = useState(""); // State for selected category
  const [loading, setLoading] = useState(true); 

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLoginObj({ ...loginObj, [name]: value });
  };

  const handleSignUp = async (e) => {
    e.preventDefault(); // Prevent default form submission
    setError(''); // Reset error state
  
    try {
      const response = await fetch('http://10.10.20.9:7071/IIA-Website-1.0.0/admin/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: loginObj.name,
          phoneNumber: loginObj.phoneNumber || "", // You can update this as needed
          emailId: loginObj.emailId,
          password: loginObj.password,
        }),
      });
  
      // Check response status
      if (!response.ok) {
        const errorMessage = await response.text();
        throw new Error(errorMessage || 'Failed to register. Please try again.');
      }
  
      // Check if the response is in JSON format
      const contentType = response.headers.get('Content-Type');
      let data;
      if (contentType && contentType.includes('application/json')) {
        data = await response.json();
        console.log('Registration successful:', data);
        
        // Navigate to the login page after successful registration
        navigate('/login');
      } else {
        // Handle non-JSON responses (e.g., plain text)
        const textResponse = await response.text();
        console.log('Response:', textResponse);
        throw new Error(textResponse); // Throw error message received in plain text
      }
  
    } catch (error) {
      console.error('Error:', error);
      setError(error.message || 'Something went wrong, please try again.'); // Update error state to show the error message
    }
  };


  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('http://10.10.20.9:7071/IIA-Website-1.0.0/enum/categories');
        const data = await response.json();
        setCategories(data); // Since API returns an array of strings, directly set it
        setLoading(false);
      } catch (error) {
        console.error("Error fetching categories:", error);
        setLoading(false);
      }
    };

    fetchCategories(); // Call the function to fetch data when the component mounts
  }, []);

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };
  
  
  // Close the success popup
//   const closePopup = () => {
//     setIsSuccessPopupVisible(false); // Close the popup
//     navigate('/login'); // Redirect to login page after closing popup
//   };

  return (
    <div className="login-body1">
      <div className="login-container1">
        {/* Login Form Section */}
        <div className="form-section1">
          <div className="form-content1">
            <img src={logo}  alt="ACTUS Logo" className="logo1" />
            <h5>Sign Up</h5>
            <div className="form-group">
      {loading ? (
        <p>Loading categories...</p>
      ) : (
        <select
          name="options"
          required
          className="custom-dropdown1"
          value={selectedCategory}
          onChange={handleCategoryChange}
        >
          <option value="" disabled>
            Category
          </option>
          {/* Dynamically render options from API data */}
          {categories.map((category, index) => (
            <option key={index} value={category}>
              {category}
            </option>
          ))}
        </select>
      )}
    </div>
            <form onSubmit={handleSignUp}>
            {/* <label>Name </label> */}
            <div className="form-group1">
                {/* <label>Email ID <span style={{ color: 'red' }}>*</span></label> */}
                <input
                  type="text"
                  name="name"
                  value={loginObj.name}
                  onChange={handleChange}
                  required
                  placeholder="Organization *"
                  className="form-control"
                />
              </div>
            <div className="form-group1">
                {/* <label>Email ID <span style={{ color: 'red' }}>*</span></label> */}
                <input
                  type="text"
                  name="name"
                  value={loginObj.name}
                  onChange={handleChange}
                  required
                  placeholder="Enter Name *"
                  className="form-control"
                />
              </div>
              {/* <label>Email ID</label> */}
              <div className="form-group1">
                {/* <label>Email ID <span style={{ color: 'red' }}>*</span></label> */}
                <input
                  type="email"
                  name="emailId"
                  value={loginObj.emailId}
                  onChange={handleChange}
                  required
                  placeholder="Enter Email ID *"
                  className="form-control"
                />
              </div>

              <div className="form-group1">
                {/* <label>Email ID <span style={{ color: 'red' }}>*</span></label> */}
                <input
                  type="MobileNo"
                  name="MobileNo"
                  value={loginObj.MobileNo}
                  onChange={handleChange}
                  required
                  placeholder="Mobile No *"
                  className="form-control"
                />
              </div>
              

              
              <div className="form-group1 password-field">
  {/* <label>Password </label> */}
  <div className="input-container1">
    <input
      type={hidePassword ? 'password' : 'text'}
      name="password"
      value={loginObj.password}
      onChange={handleChange}
      required
      placeholder="Enter Password *"
      className="form-control"
    />
    <button 
      type="button" 
      className="toggle-password" 
      onClick={() => setHidePassword(prev => !prev)}
    >
      {hidePassword ? (
        <i className="fa fa-eye-slash" aria-hidden="true"></i> // Eye slash icon for hidden password
      ) : (
        <i className="fa fa-eye" aria-hidden="true"></i> // Eye icon for visible password
      )}
    </button>
  </div>
</div>

              <div className="form-footer1">
            
                <button type="submit" className="btn1 btn1-primary">
                  Signup
                </button>
              </div>
            </form>
            
            {error && <span className="error-text">{error}</span>}
          </div>
        </div>
      </div>
      {/* {isSuccessPopupVisible && (
  <>
    <div className="popup-overlay"></div>
    <div className="popup">
      <div className="popup-content">
        <h3>Registration Successful!</h3>
        <p>You have successfully registered.</p>
        <button onClick={closePopup} className="btn btn-secondary">
          Close
        </button>
      </div>
    </div>
  </>
)} */}

    </div>
  );
};

export default SignUpEmp ;
