import React, { useState,useEffect } from 'react';
import logo from "../images/logo-white-2.png";
import { useNavigate } from 'react-router-dom';

const LoginEmp = ({ onLogin }) => {
  const [loginObj, setLoginObj] = useState({identifier: '', password: '' });
  const [hidePassword, setHidePassword] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate(); 
  const [loading, setLoading] = useState(true); 
  const [selectedCategory, setSelectedCategory] = useState(""); // State for selected category
  const [categories, setCategories] = useState([]); 
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setLoginObj({ ...loginObj, [name]: value });
  };



  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };


  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('http://10.10.20.9:7071/IIA-Website-1.0.0/enum/categories');
        const data = await response.json();
        setCategories(data); // Since API returns an array of strings, directly set it
        setLoading(false);
      } catch (error) {
        console.error("Error fetching categories:", error);
        setLoading(false);
      }
    };

    fetchCategories(); // Call the function to fetch data when the component mounts
  }, []);
  



  const handleLoginSubmit = async (e) => {
    e.preventDefault();
   console.log()
    // Ensure both emailId and password are filled
    if (!loginObj.identifier || !loginObj.password) {
      setError('Email and password are required.');
      return;
    }

    try {
      // API call to login
      const response = await fetch("http://10.10.20.9:7071/IIA-Website-1.0.0/login/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(loginObj), // Send login data
      });
      console.log(loginObj);
      const result = await response.json();

      if (response.ok) {
        // On successful login, store user data and token
        alert("Login successful!");
      
        onLogin(); // Call login handler if needed
        navigate("/"); // Navigate to home page after login
      } else {
        setError(result.message || "Login failed, please try again.");
      }
    } catch (error) {
      alert(e)
      console.error("Login error:", error);
      setError("Something went wrong, please try again later.");
    }
  };



  return (
    <div className="login-body">
      <div className="login-container">
        {/* Login Form Section */}
        <div className="form-section">
          <div className="form-content">
            <img src={logo}  alt="ACTUS Logo" className="logo" />
            <h5>Sign Into Your Account</h5>
            <div className="form-group">
      {loading ? (
        <p>Loading categories...</p>
      ) : (
        <select
          name="options"
          required
          className="custom-dropdown1"
          value={selectedCategory}
          onChange={handleCategoryChange}
        >
          <option value="" disabled>
            Category
          </option>
          {/* Dynamically render options from API data */}
          {categories.map((category, index) => (
            <option key={index} value={category}>
              {category}
            </option>
          ))}
        </select>
      )}
    </div>



            <form onSubmit={handleLoginSubmit}>
              <div className="form-group">
                {/* <label>Email ID <span style={{ color: 'red' }}>*</span></label> */}
                <input
                  type="email"
                  name="identifier"
                  value={loginObj.identifier}
                  onChange={handleChange}
                  required
                  placeholder="Enter Email ID/Mobile No *"
                  className="form-control"
                />
              </div>

              
              <div className="form-group password-field">
 
  <div className="input-container">
    <input
      type={hidePassword ? 'password' : 'text'}
      name="password"
      value={loginObj.password}
      onChange={handleChange}
      required
      placeholder="Enter password *"
      className="form-control"
    />
    <button 
      type="button" 
      className="toggle-password" 
      onClick={() => setHidePassword(prev => !prev)}
    >
      {hidePassword ? (
        <i className="fa fa-eye-slash" aria-hidden="true"></i> // Eye slash icon for hidden password
      ) : (
        <i className="fa fa-eye" aria-hidden="true"></i> // Eye icon for visible password
      )}
    </button>
  </div>
</div>

              <div className="form-footer">
                <a href="/forgot" className="forgot-password">
                  Forgot Password?
                </a>
                <button type="submit" className="btn2 btn2-primary">
                  Login
                </button>
              </div>
            </form>
            <p className="signup-text">
              Don't have an account? <a href="/signup" className="signup-link">Signup</a>
            </p>
            {error && <span className="error-text">{error}</span>}
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginEmp;
