import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';


function TopNavBar() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [subNavData, setSubNavData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navId = "40"; // Replace with actual `navId` or pass as prop if dynamic

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  useEffect(() => {
    const fetchSubNavData = async () => {
      try {
        const response = await fetch(`http://10.10.20.9:7071/IIA-Website-1.0.0/subsidenavbar/subnav/${navId}`);
        if (!response.ok) throw new Error('Failed to fetch sub-navigation data');

        const data = await response.json();
        setSubNavData(Array.isArray(data) ? data : [data]);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchSubNavData();
  }, [navId]);

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <Link to="/">MyApp</Link>
      </div>
      <div className={`navbar-links ${isMenuOpen ? 'active' : ''}`}>
        <Link to="/">Home</Link>
        <Link to="/about">About</Link>
        <Link to="/services">Services</Link>

        {/* Display fetched navigation links */}
        {loading ? (
          <p>Loading...</p>
        ) : error ? (
          <p>Error: {error}</p>
        ) : (
          subNavData.map((item) => (
            <Link key={item.subNavId} to={`/${item.name.replace(/\s+/g, '-').toLowerCase()}/${item.subNavId}`}>
              {item.name}
            </Link>
          ))
        )}
        <button className="login-button">Login</button>
      </div>
      <div className="menu-toggle" onClick={toggleMenu}>
        <span className="bar"></span>
        <span className="bar"></span>
        <span className="bar"></span>
      </div>
    </nav>
  );
}

export default TopNavBar;
