import { Category } from '@mui/icons-material';
import React, { useEffect, useState } from 'react';
import { useParams, Link,useLocation  } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import * as XLSX from 'xlsx';
import { CloudUpload, CloudDownload, CheckCircleOutline, RemoveRedEye, Edit } from '@mui/icons-material';
import {
    Card,
    CardHeader,
    CardContent,
    CardActions,
    Button,
    Checkbox,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    TablePagination,
    TextField,
    IconButton,
} from '@mui/material';


const Layout1 = () => {

    // const datasource = [
    //     {
    //         SNo: '1',
    //         Category: "",
    //         Organization: "",
    //         Name: 'Kumar Ravi',
    //         contactNumber: '9876543210',
    //         emailId: 'rcreddy2222@gmail.com',
    //         Role: '',
    //         Action: '',
    //         Remarks: '',

    //     },

    // ];
    const [datasource, setDatasource] = useState([]);
    const [filter, setFilter] = useState('');
    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const [isPopupOpen1, setIsPopupOpen1] = useState(false);
    const [selected, setSelected] = useState([]); 
    const [selectedRowData, setSelectedRowData] = useState(null);
  const [selectedSubNavId, setSelectedSubNavId] = useState(null);
  const [page, setPage] = useState(0); 
  const [rowsPerPage, setRowsPerPage] = useState(5);
  const { navId: routeNavId } = useParams(); // Get navId from route parameters
  const [navId, setNavId] = useState(() => (routeNavId ? Number(routeNavId) : undefined)); // Initialize navId with routeNavId
  const [isSelected, setIsSelected] = useState(false); 
  const [directoryNavId, setDirectoryNavId] = useState(null);
  const navigate = useNavigate();
  const [userDetails, setUserDetails] = useState({
        organisation: "",
        name: "",
        email: "",
        phoneNumber: "",
        role: "",
        categories: ""
    
  });


  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const response = await fetch('http://10.10.20.9:7071/IIA-Website-1.0.0/registration/getdetailsforview/1');
        if (!response.ok) {
          throw new Error('Failed to fetch user details');
        }
        const data = await response.json();
        setUserDetails({
          organisation:data.organisation || '',
          name: data.name || '',
          email: data.email || '',
          mobile: data.phoneNumber || '',
          role:data.role || '',
          categories:data.categories || '',
        });
      } catch (error) {
        console.error(error);
      } finally {
        // setLoading(false);
      }
    };

    fetchUserDetails();
  }, []);

 
  useEffect(() => {
    const fetchData = async () => {
        try {
            const response = await fetch('http://10.10.20.9:7071/IIA-Website-1.0.0/userData/getall'); // Replace with your API URL
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setDatasource(data); // Set fetched data to the datasource state
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    fetchData();
}, []); // Empty dependency array means this effect runs once on mount


useEffect(() => {
    const fetchCategories = async () => {
        try {
            const response = await fetch('http://10.10.20.9:7071/IIA-Website-1.0.0/enum/categoryies');
            const data = await response.json();
            setCategories(data); // Assuming API returns an array of strings
            setLoading(false);
        } catch (error) {
            console.error("Error fetching categories:", error);
            setLoading(false);
        }
    };

    fetchCategories();
}, []);


const AddRecord = async (e) => {
    e.preventDefault(); // Prevent form from reloading the page
    try {
        const response = await fetch('http://10.10.20.9:7071/IIA-Website-1.0.0/userData/register', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                organisation: userDetails.organisation,
                name: userDetails.name,
                phoneNumber: userDetails.phoneNumber,
                email: userDetails.emailId,
                password: userDetails.password,
                categories: userDetails.categories,
            }),
        });

        if (!response.ok) {
            const errorMessage = await response.text();
            throw new Error(errorMessage || 'Failed to register. Please try again.');
        }

        const contentType = response.headers.get('Content-Type');
        if (contentType && contentType.includes('application/json')) {
            const data = await response.json();
            console.log('Registration successful:', data);
            navigate('/layout1')
            window.location.reload();
            alert('Data Successfully Saved');
            setIsPopupOpen(false);  // Close the popup
           
        } else {
            const textResponse = await response.text();
            console.log('Response:', textResponse);
            throw new Error(textResponse);
        }
    } catch (error) {
        console.error('Error:', error);
        // setError(error.message || 'Something went wrong, please try again.');
    }
};



const updateRecord = async (e) => {
    e.preventDefault(); // Prevent default form submission
    try {
        const response = await fetch(`http://10.10.20.9:7071/IIA-Website-1.0.0/userData/${selectedRowData.newId}`, {
            method: 'PUT', // Change to PUT for updating
            headers: {
                'Content-Type': 'application/json',
            },


        
            body: JSON.stringify(selectedRowData),
        });


       
        if (!response.ok) {
            const errorMessage = await response.text();
            throw new Error(errorMessage || 'Failed to update. Please try again.');
        }

        const updatedData = await response.json();
        console.log('Update successful:', updatedData);
        window.location.reload();
        alert('Record updated successfully');
        setIsPopupOpen1(false);  // Close the popup
        // Optionally navigate or refresh data here
      
    } catch (error) {
        console.error('Error:', error);
        alert(error.message || 'Something went wrong, please try again.'); // Display error message
    }
};




  const handlePopup= () => {
    setIsPopupOpen(true);
};

const handlePopup1= (rowData) => {
    // alert(rowData)
    setSelectedRowData(rowData);
    setIsPopupOpen1(true);
};


  const exportToExcel = () => {
    // Convert data to a worksheet
    const worksheet = XLSX.utils.json_to_sheet(datasource);
    
    // Create a new workbook and append the worksheet
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Data');

    // Generate a binary Excel file
    XLSX.writeFile(workbook, 'exported_data.xlsx');
};



  const handleClick = (event, row) => {
        const selectedIndex = selected.indexOf(row.candidateId);
        let newSelected = [];
    
        if (selectedIndex === -1) {
          newSelected = newSelected.concat(selected, row.candidateId);
        } else if (selectedIndex === 0) {
          newSelected = newSelected.concat(selected.slice(1));
        } else if (selectedIndex === selected.length - 1) {
          newSelected = newSelected.concat(selected.slice(0, -1));
        } else if (selectedIndex > 0) {
          newSelected = newSelected.concat(
            selected.slice(0, selectedIndex),
            selected.slice(selectedIndex + 1),
          );
        }
    
        setSelected(newSelected);
      };


      const handlePageChange = (event, newPage) => {
            setPage(newPage);
          };
        
          const handleRowsPerPageChange = (event) => {
            setRowsPerPage(parseInt(event.target.value, 10));
            setPage(0);
          };



  const handleFilterChange = (event) => {
        setFilter(event.target.value);
      };
    
      const handleSelectAllClick = (event) => {
        if (event.target.checked) {
          const newSelected = datasource.map((row) => row.candidateId);
          setSelected(newSelected);
        } else {
          setSelected([]);
        }
      };
    

    return (
        <Card style={{ marginLeft: 'auto', marginRight: '100px', width: 'fit-content', padding: '20px',marginTop:'20px' }}>
      <CardHeader
    style={{
        backgroundColor: 'whitesmoke',
        marginTop: '2%',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
    }}
    title={
        <TextField
            placeholder="Filter"
            value={filter}
            onChange={handleFilterChange}
            className="filterAlignment"
            style={{ width: '30%', height: '32px', margin: 0 }} // Adjust height here
            variant="outlined" // Try using outlined or standard variant
            InputProps={{
                style: {
                    padding: '4px 10px', // Adjust padding for height
                    height: '38px', // Ensure this matches the height you want
                },
                inputProps: {
                    style: { height: '38px' } // Set the height of the input element itself
                }
            }}
        />
    }
    action={
        <>
            <input
                type="file"
                accept=".csv, application/vnd.openxmlformats-officedocument.spreadsheetml.sheet, application/vnd.ms-excel"
                style={{ display: 'none' }}
                id="fileUpload"
            />
            <label htmlFor="fileUpload">
                <Button variant="extendedFab" component="span" style={{ backgroundColor: 'blue', color: 'white', marginRight: '10px' }}>
                    <CloudDownload style={{ marginRight: '8px' }} />
                    Import
                </Button>
            </label>
            <Button variant="extendedFab" onClick={exportToExcel} style={{ backgroundColor: 'blue', color: 'white', marginRight: '10px' }}>
                <CloudUpload style={{ marginRight: '8px' }} />
                Export
            </Button>
            <Button variant="extendedFab" onClick={handlePopup} style={{ backgroundColor: 'blue', color: 'white' }}>
                + Add
            </Button>
        </>
   
    

            }
        />
        <TableContainer style={{ marginTop: '20px', border: '1px solid rgba(0, 0, 0, 0.12)' }}>
    <Table>
        <TableHead style={{ backgroundColor: 'lightblue' }}>
            <TableRow>
                <TableCell padding="checkbox" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>
                    <Checkbox
                        indeterminate={selected.length > 0 && selected.length < datasource.length}
                        checked={datasource.length > 0 && selected.length === datasource.length}
                        onChange={handleSelectAllClick}
                    />
                </TableCell>
                {['S.No', 'Category', 'Organization', 'Name', 'Contact Number', 'Email Id', 'Role', 'Remarks','Action'].map((heading, index) => (
                    <TableCell 
                        align="center" 
                        key={index} 
                        style={{ borderRight: index !== 8 ? '1px solid rgba(0, 0, 0, 0.12)' : undefined }}
                    >
                        {heading}
                    </TableCell>
                ))}
                {/* <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>Actions</TableCell> */}
            </TableRow>
        </TableHead>
        <TableBody>
    {datasource
        .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
        .map((row, index) => (
            <TableRow
                hover
                onClick={(event) => handleClick(event, row)}
                role="checkbox"
                tabIndex={-1}
                key={row.newId}
            >
                <TableCell padding="checkbox" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>
                    <Checkbox
                        checked={selected.indexOf(row.candidateId) !== -1}
                        onChange={() => handleClick(null, row)}
                    />
                </TableCell>
                <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.newId}</TableCell>
                <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.categories}</TableCell>
                <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.organisation}</TableCell>
                <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.name}</TableCell>
                <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.phoneNumber}</TableCell>
                <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.email}</TableCell>
                <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.role}</TableCell>
                <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.Remarks}</TableCell>
                <TableCell align="center">
                    <IconButton onClick={() => console.log('View', row.candidateId)}>
                        <RemoveRedEye style={{ color: 'green' }} />
                    </IconButton>
                    <IconButton onClick={() => handlePopup1(row)}>
                        <Edit />
                    </IconButton>
                </TableCell>
            </TableRow>
        ))}
</TableBody>
    </Table>
</TableContainer>


{isPopupOpen && (
                <div className="modal-overlay">
                    <div className="modal-content1">
                      
                    <div className="header-row">
                <div className="serial-number">S No-[0]</div>
                <button className="close-button1" onClick={() => setIsPopupOpen(false)}>&times;</button>
            </div>
                        <form className="form-grid" onSubmit={AddRecord}>
                        <div className="form-group">
            {loading ? (
                <p>Loading categories...</p>
            ) : (
                <select
                    className="form-control"
                    required
                    value={userDetails.categories}
                    onChange={handleCategoryChange}
                >
                    <option value="" disabled>Select a category</option>
                    {categories.map((category, index) => (
                        <option key={index} value={category}>
                            {category}
                        </option>
                    ))}
                </select>
            )}
        </div>
                            <div className="form-group">
                                <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Organization"
                                    required
                                    onChange={(e) => setUserDetails({ ...userDetails, organisation: e.target.value })} // Handle input change
                                />
                            </div>
                            <div className="form-group">
                                <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Name"
                                    required
                                    onChange={(e) => setUserDetails({ ...userDetails, name: e.target.value })} // Handle input change
                                />
                            </div>
                            <div className="form-group">
                                <input
                                    type="tel" // Changed to 'tel' for better contact number validation
                                    className="form-control"
                                    placeholder="Contact Number"
                                    required
                                    onChange={(e) => setUserDetails({ ...userDetails, phoneNumber: e.target.value })} // Handle input change
                                />
                            </div>
                            <div className="form-group">
                                <input
                                    type="email"
                                    className="form-control"
                                    placeholder="Email Id"
                                    required
                                    onChange={(e) => setUserDetails({ ...userDetails, emailId: e.target.value })} // Handle input change
                                />
                            </div>
                            <div className="form-group">
                                <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Role"
                                    required
                                    onChange={(e) => setUserDetails({ ...userDetails, role: e.target.value })} // Handle input change
                                />
                            </div>
                            <div className="form-group">
                                <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Remarks"
                                    required
                                    onChange={(e) => setUserDetails({ ...userDetails, remarks: e.target.value })} // Handle input change
                                />
                            </div>
                            <div className="button-group">
                                <button type="button" className="btn btn-cancel" onClick={() => setIsPopupOpen(false)}>Cancel</button>
                                <button type="button" className="btn btn-save">Save</button>
                                <button type="submit" className="btn btn-submit">Submit</button>
                            </div>
                        </form>
                    </div>
                </div>

)}



{isPopupOpen1  &&  selectedRowData && ( // Ensure itemData is defined before rendering
             
             <div className="modal-overlay">
  <div className="modal-content1">
                    
                      
  <div className="header-row">
                      <div class="serial-number">S No-{selectedRowData.newId}</div>
                      <button class="close-button1" onClick={() => setIsPopupOpen1(false)}>&times;</button>
                    </div>
                      <form className="form-grid" onSubmit={updateRecord}>
                            <div className="form-group">
                                <input type="text" className="form-control" value={selectedRowData.categories} placeholder="Category" required onChange={(e) => setSelectedRowData({ ...selectedRowData, categories: e.target.value })} />
                            </div>
                            <div className="form-group">
                                <input type="text" className="form-control" value={selectedRowData.organisation} placeholder="Organization" required onChange={(e) => setSelectedRowData({ ...selectedRowData, organisation: e.target.value })} />
                            </div>
                            <div className="form-group">
                                <input type="text" className="form-control" value={selectedRowData.name} placeholder="Name" required onChange={(e) => setSelectedRowData({ ...selectedRowData, name: e.target.value })} />
                            </div>
                            <div className="form-group">
                                <input type="number" className="form-control" value={selectedRowData.phoneNumber} placeholder="Contact Number" required onChange={(e) => setSelectedRowData({ ...selectedRowData, phoneNumber: e.target.value })} />
                            </div>
                            <div className="form-group">
                                <input type="email" className="form-control" value={selectedRowData.email} placeholder="Email Id" required onChange={(e) => setSelectedRowData({ ...selectedRowData, email: e.target.value })} />
                            </div>
                            <div className="form-group">
                                <input type="text" className="form-control" value={selectedRowData.role} placeholder="Role" required onChange={(e) => setSelectedRowData({ ...selectedRowData, role: e.target.value })} />
                            </div>
                            <div className="form-group">
                                <input type="text" className="form-control" value={selectedRowData.remarks} placeholder="Remarks" required onChange={(e) => setSelectedRowData({ ...selectedRowData, remarks: e.target.value })} />
                            </div>
                            <div className="button-group">
                                <button type="button" className="btn btn-cancel" onClick={() => setIsPopupOpen1(false)}>Cancel</button>
                                <button type="submit" className="btn btn-save">Save</button>
                            </div>
                        </form>
                  </div>
              </div>
              
            )}






        <CardActions>
            <TablePagination
                rowsPerPageOptions={[5, 10, 25, 100]}
                component="div"
                count={datasource.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onPageChange={handlePageChange}
                onRowsPerPageChange={handleRowsPerPageChange}
            />
        </CardActions>
    </Card>  




    )

    
}
export default Layout1;
