import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";
import SignUpEmp from "./Component/SignUp";
import Login from "./Component/Login";
import Logout from "./Component/Logout";
import ForgotPassword from "./Component/ForgotPassword";
import Home from "./Component/Home";
import NavBar from "./Component/NavBar";
import SideNavBar from "./Component/SideNavBar";
import TopNavBar from "./Component/TopNavBar";
import ControllersTableData from "./Component/ControllersTableData";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const authStatus = localStorage.getItem('isAuthenticated');
    if (authStatus) {
      setIsAuthenticated(JSON.parse(authStatus));
    }
  }, []);

  const handleLogin = () => {
    setIsAuthenticated(true);
    localStorage.setItem("isAuthenticated", "true");
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    localStorage.removeItem("isAuthenticated");
  };

  // This is the component that will use the location
  function AppContent() {
    const currentLocation = useLocation();

    return (
      <>
        {isAuthenticated && <NavBar />}
        {isAuthenticated && <SideNavBar />}
        {isAuthenticated && currentLocation.pathname !== "/controllers-table-data" && <TopNavBar />}

        <Routes>
          <Route path="/signup" element={<SignUpEmp />} />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/logout" element={<Logout onLogout={handleLogout} />} />
          <Route path="/forgot" element={<ForgotPassword />} />
          <Route path="/topnavbar" element={<TopNavBar />} />
          <Route path="/home" element={<Home />} />
          <Route path="/controllers-table-data" element={<ControllersTableData />} />
        </Routes>
      </>
    );
  }

  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
