import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import NavBar from "./Component/NavBar";
import Footer from "./Component/Footer";
import Home from "./Component/Home";
import Logout from "./Component/Logout";
import Sidenav from "./Component/Sidenav";
import ControllersTableData from "./Component/ControllersTableData"
import LoginEmp from "./Component/Login";
import SignUpEmp from "./Component/SignUp";
import ForgotEmp from "./Component/ForgotPassword";
import OnBoarding from "./Component/OnBoarding";
import Profile from "./Component/Profile";
import EmptyPage from "./Component/EmptyPage";

const courseComponents = {


  // Add more mappings as needed
};

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Load auth status from localStorage once when the app starts
  // useEffect(() => {
  //   const storedAuth = localStorage.getItem("isAuthenticated");
  //   setIsAuthenticated(storedAuth === "true");
  // }, []);

  const handleLogin = () => {
    setIsAuthenticated(true);
    localStorage.setItem("isAuthenticated", "true");
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    localStorage.removeItem("isAuthenticated");
  };


  const subNavItems = [
    { subNavId: 1, name: 'Item 1', component: DynamicPage },
    { subNavId: 2, name: 'Item 2', component: DynamicPage },
    // Add more items as needed
  ];

  return (
    <Router>
      {/* Render NavBar and Sidenav only if authenticated */}
      {isAuthenticated && <NavBar />}
      {isAuthenticated && <Sidenav />}

      <Routes>
        {/* Redirect to login if not authenticated various procedures  */}
        
        {/* <Route path="/" element={isAuthenticated ? <Home /> : <Navigate to="/login" replace />} /> */}
        <Route path="/" element={<Home />} />
        <Route path="/logout" element={<Logout onLogout={handleLogout} />} />
        <Route path="/login" element={<LoginEmp onLogin={handleLogin} />} />
        <Route path="/signup" element={<SignUpEmp />} />
        <Route path="/forgot" element={<ForgotEmp  />} />
        <Route path="/onboarding/:navId" element={<OnBoarding subNavItems={subNavItems} />}>
          {/* Dynamically create sub-routes based on subNavItems */}
          {subNavItems.map((subNav) => {
            const dynamicRoute = `/${subNav.name.replace(/\s+/g, '-').toLowerCase()}/${subNav.subNavId}`;
            return (
              <Route 
                key={subNav.subNavId} 
                path={dynamicRoute} 
                element={<subNav.component />} 
              />
            );
          })}
        </Route>
        {isAuthenticated && (
          <>
          
           <Route path="/Profile/:navId" element={<Profile />} />
           {/* <Route path="/onboarding/:navId" element={<OnBoarding />} /> */}
           <Route path="/EmptyPage" element={<EmptyPage />} />

            <Route path="/cntrollers-table-data" element={<ControllersTableData />} />

            {Object.keys(courseComponents).map((id) => (
              <Route key={id} path={`/all-courses/${id}`} element={React.createElement(courseComponents[id])} />
            ))}

          </>
        )}
      </Routes>

      {/* {isAuthenticated && <Footer />} */}
    </Router>
  );
}

export default App;
