import { Category } from '@mui/icons-material';
import React, { useEffect, useState } from 'react';
import { useParams, Link,useLocation  } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import * as XLSX from 'xlsx';
import { CloudUpload, CloudDownload, CheckCircleOutline, RemoveRedEye, Edit } from '@mui/icons-material';
import {
    Card,
    CardHeader,
    CardContent,
    CardActions,
    Button,
    Checkbox,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    TablePagination,
    TextField,
    IconButton,
} from '@mui/material';


const Layout1 = () => {

    const datasource = [
        {
            SNo: '1',
            Category: "",
            Organization: "",
            Name: 'Kumar Ravi',
            contactNumber: '9876543210',
            emailId: 'rcreddy2222@gmail.com',
            Role: '',
            Action: '',
            Remarks: '',

        },

    ];

    const [filter, setFilter] = useState('');
    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const [selected, setSelected] = useState([]); 
  const [selectedSubNavId, setSelectedSubNavId] = useState(null);
  const [page, setPage] = useState(0); 
  const [rowsPerPage, setRowsPerPage] = useState(5);
  const { navId: routeNavId } = useParams(); // Get navId from route parameters
  const [navId, setNavId] = useState(() => (routeNavId ? Number(routeNavId) : undefined)); // Initialize navId with routeNavId
  const [isSelected, setIsSelected] = useState(false); 
  const [directoryNavId, setDirectoryNavId] = useState(null);
  const navigate = useNavigate();

 

  const handlePopup= () => {
    setIsPopupOpen(true);
};


  const exportToExcel = () => {
    // Convert data to a worksheet
    const worksheet = XLSX.utils.json_to_sheet(datasource);
    
    // Create a new workbook and append the worksheet
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Data');

    // Generate a binary Excel file
    XLSX.writeFile(workbook, 'exported_data.xlsx');
};



  const handleClick = (event, row) => {
        const selectedIndex = selected.indexOf(row.candidateId);
        let newSelected = [];
    
        if (selectedIndex === -1) {
          newSelected = newSelected.concat(selected, row.candidateId);
        } else if (selectedIndex === 0) {
          newSelected = newSelected.concat(selected.slice(1));
        } else if (selectedIndex === selected.length - 1) {
          newSelected = newSelected.concat(selected.slice(0, -1));
        } else if (selectedIndex > 0) {
          newSelected = newSelected.concat(
            selected.slice(0, selectedIndex),
            selected.slice(selectedIndex + 1),
          );
        }
    
        setSelected(newSelected);
      };


      const handlePageChange = (event, newPage) => {
            setPage(newPage);
          };
        
          const handleRowsPerPageChange = (event) => {
            setRowsPerPage(parseInt(event.target.value, 10));
            setPage(0);
          };



  const handleFilterChange = (event) => {
        setFilter(event.target.value);
      };
    
      const handleSelectAllClick = (event) => {
        if (event.target.checked) {
          const newSelected = datasource.map((row) => row.candidateId);
          setSelected(newSelected);
        } else {
          setSelected([]);
        }
      };
    

    return (
        <Card style={{ marginLeft: 'auto', marginRight: '40px', width: 'fit-content', padding: '20px',marginTop:'20px' }}>
      <CardHeader
    style={{
        backgroundColor: 'whitesmoke',
        marginTop: '2%',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
    }}
    title={
        <TextField
            placeholder="Filter"
            value={filter}
            onChange={handleFilterChange}
            className="filterAlignment"
            style={{ width: '30%', height: '32px', margin: 0 }} // Adjust height here
            variant="outlined" // Try using outlined or standard variant
            InputProps={{
                style: {
                    padding: '4px 10px', // Adjust padding for height
                    height: '38px', // Ensure this matches the height you want
                },
                inputProps: {
                    style: { height: '38px' } // Set the height of the input element itself
                }
            }}
        />
    }
    action={
        <>
            <input
                type="file"
                accept=".csv, application/vnd.openxmlformats-officedocument.spreadsheetml.sheet, application/vnd.ms-excel"
                style={{ display: 'none' }}
                id="fileUpload"
            />
            <label htmlFor="fileUpload">
                <Button variant="extendedFab" component="span" style={{ backgroundColor: 'blue', color: 'white', marginRight: '10px' }}>
                    <CloudDownload />
                    Import
                </Button>
            </label>
            <Button variant="extendedFab" onClick={exportToExcel} style={{ backgroundColor: 'blue', color: 'white', marginRight: '10px' }}>
                <CloudUpload />
                Export
            </Button>
            <Button variant="extendedFab" onClick={handlePopup} style={{ backgroundColor: 'blue', color: 'white' }}>
                +Add
            </Button>
        </>
   
    

            }
        />
        <TableContainer style={{ marginTop: '20px', border: '1px solid rgba(0, 0, 0, 0.12)' }}>
    <Table>
        <TableHead style={{ backgroundColor: 'lightblue' }}>
            <TableRow>
                <TableCell padding="checkbox" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>
                    <Checkbox
                        indeterminate={selected.length > 0 && selected.length < datasource.length}
                        checked={datasource.length > 0 && selected.length === datasource.length}
                        onChange={handleSelectAllClick}
                    />
                </TableCell>
                {['S.No', 'Category', 'Organization', 'Name', 'Contact Number', 'Email Id', 'Role', 'Action', 'Remarks'].map((heading, index) => (
                    <TableCell 
                        align="center" 
                        key={index} 
                        style={{ borderRight: index !== 8 ? '1px solid rgba(0, 0, 0, 0.12)' : undefined }}
                    >
                        {heading}
                    </TableCell>
                ))}
                <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>Actions</TableCell>
            </TableRow>
        </TableHead>
        <TableBody>
            {datasource
                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                .map((row, index) => (
                    <TableRow
                        hover
                        onClick={(event) => handleClick(event, row)}
                        role="checkbox"
                        tabIndex={-1}
                        key={row.candidateId}
                    >
                        <TableCell padding="checkbox" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>
                            <Checkbox
                                checked={selected.indexOf(row.candidateId) !== -1}
                                onChange={() => handleClick(null, row)}
                            />
                        </TableCell>
                        <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.SNo}</TableCell>
                        <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.Category}</TableCell>
                        <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.Organization}</TableCell>
                        <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.Name}</TableCell>
                        <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.contactNumber}</TableCell>
                        <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.emailId}</TableCell>
                        <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.Role}</TableCell>
                        <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.action}</TableCell>
                        <TableCell align="center" style={{ borderRight: '1px solid rgba(0, 0, 0, 0.12)' }}>{row.Remarks}</TableCell>
                        <TableCell align="center">
                            <IconButton onClick={() => console.log('View', row.candidateId)}>
                                <RemoveRedEye style={{ color: 'green' }} />
                            </IconButton>
                            <IconButton onClick={() => console.log('Edit', row.candidateId)}>
                                <Edit />
                            </IconButton>
                        </TableCell>
                    </TableRow>
                ))}
        </TableBody>
    </Table>
</TableContainer>


{isPopupOpen  && ( // Ensure itemData is defined before rendering
             
             <div className="modal-overlay">
  <div className="modal-content1">
                      <button class="close-button" onclick="closePopup()">&times;</button>
                      
                     
                      <div class="serial-number">S No-[0]</div>
                      
                    
                      <form class="form-grid">
                         
                          <div class="form-group">
                              
                              <input type="text" class="form-control" placeholder="Category" required />
                          </div>
                          <div class="form-group">
                              
                              <input type="text" class="form-control"   placeholder="Organization" required />
                          </div>
                          <div class="form-group">
                              
                              <input type="text" class="form-control"  placeholder="Name" required />
                          </div>
              
                          
                          <div class="form-group">
                             
                              <input type="email" class="form-control"  placeholder="Contact Number" required />
                          </div>
                          <div class="form-group">
                             
                              <input type="number" class="form-control"  placeholder="Email Id" required />
                          </div>
                          <div class="form-group">
                              
                              <input type="text" class="form-control"  placeholder="Role" required />
                          </div>
              
                        
                          <div class="form-group">
                            
                              <input type="text" class="form-control"  placeholder="Remarks" required />
                          </div>
                        
              
                         
                          <div class="button-group">
                              <button type="button" class="btn btn-cancel" onClick={() => setIsPopupOpen(false)}>Cancel</button>
                              <button type="button" class="btn btn-save">Save</button>
                              <button type="submit" class="btn btn-submit">Submit</button>
                          </div>
                      </form>
                  </div>
              </div>
              
            )}

        <CardActions>
            <TablePagination
                rowsPerPageOptions={[5, 10, 25, 100]}
                component="div"
                count={datasource.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onPageChange={handlePageChange}
                onRowsPerPageChange={handleRowsPerPageChange}
            />
        </CardActions>
    </Card>  




    )

    
}
export default Layout1;
