// Create the SidebarContext
const SidebarContext = createContext();

// Custom hook to use the Sidebar context
export const useSidebar = () => {
  return useContext(SidebarContext);
};
const Sidenav = ({ isHidden }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const [isSecondModalOpen, setIsSecondModalOpen] = useState(false); // Second modal state
  const navigate = useNavigate();
  const [imagePreview, setImagePreview] = useState(null);
  const [image, setImage] = useState('');
  const [name, setName] = useState('');
  const [loading, setLoading] = useState(true);
  const [posterImage, setPosterImage] = useState('');
  const [apiData, setApiData] = useState([]);

  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // Sidebar toggle state
  const [selectedCourse, setSelectedCourse] = useState(null); // Track selected course 


  const [navId, setNavId] = useState(1); // Initialize navId with a default value
  const [subNavId, setSubNavId] = useState(1); // Initialize subNavId with a default value

  const [selectedCourseId, setSelectedCourseId] = useState(null); // Track selected cour
  const [subNavData, setSubNavData] = useState([]);



  const toggleSidenav = () => {
    setIsSidebarOpen((prev) => !prev);
  };


  // Fetch sub-navigation data based on navId
  const fetchSubNavData = async (navId) => {
    if (!navId) {
      console.error('navId is not set');
      return; // Exit if navId is not defined
    }

    try {
      const response = await fetch(`http://10.10.20.9:7071/IIA-Website-1.0.0/subsidenavbar/subnav/${navId}`);
      if (!response.ok) {
        throw new Error('Failed to fetch sub-navigation data: ' + response.statusText);
      }
      const data = await response.json();
      console.log('Fetched sub-navigation data:', data); // Log fetched data for debugging
      setSubNavData(data); // Set the fetched data to state
    } catch (error) {
      console.error('Error fetching sub-navigation data:', error);
    }
  };

  // Handle course click, set the selected course id
  const handleCourseClick = (course) => {
    if (selectedCourseId === course.navId) {
      // Close the sidebar if the same course is clicked again
      setSelectedCourseId(null);
      setSubNavData([]); // Clear subNavData when closing
    } else {
      // Set selected course ID and fetch sub-navigation data
      setSelectedCourseId(course.navId);
      fetchSubNavData(course.navId); // Fetch sub-navigation data for the clicked course
  
      // Determine the dynamic route based on the course name
      const route = getCourseRoute(course.name);
      
      // Navigate to the determined dynamic page
      navigate(route);
    }
  };
  





  // Form submission handler
  const handleSubmitss = async (e) => {
    e.preventDefault();

    // Prepare the data to be sent to the API
    const requestBody = {
      navId, // Use the navId from the selected course
      name,
    };

    // POST API call
    try {
      const response = await fetch('http://10.10.20.9:7071/IIA-Website-1.0.0/subsidenavbar/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });

      if (!response.ok) {
        throw new Error('Failed to submit the form');
      }

      const data = await response.json();
      console.log('Form submitted successfully:', data);

      // Handle the success, close modal, or reset form
      setIsSecondModalOpen(false);
      setName('');
    } catch (error) {
      console.error('Error submitting the form:', error);
    }
  };













  useEffect(() => {
    return () => {
      if (imagePreview) {
        URL.revokeObjectURL(imagePreview);
      }
    };
  }, [imagePreview]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://10.10.20.9:7071/IIA-Website-1.0.0/sidenav/all');
        console.log('API Response:', response.data); // Log the response data
        setApiData(response.data); // Set the fetched data to state
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false); // Ensure loading is set to false regardless of success or error
      }
    };

    fetchData();
  }, []);

  const handleSubmits = async (event) => {
    event.preventDefault();
    const cleanImage = image.replace(/^data:image\/[a-zA-Z]+;base64,/, '');
    const formData = {
      name: name,
      image: cleanImage,
    };

    try {
      const response = await axios.post(
        'http://10.10.20.9:7071/IIA-Website-1.0.0/sidenav/add',
        formData,
        {
          headers: {
            'Content-Type': 'application/json',
          },
        }
      );
      console.log('Form submitted successfully:', response.data);
      alert('Data added successfully!');
      navigate('/login');
    } catch (error) {
      console.error('Error submitting form:', error.response?.data || error.message);
    }
  };

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImage(reader.result);
        setPosterImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };




  
  const getCourseRoute = (courseName) => {
    switch (courseName) {
      case "COURSES":
        return "/COURSES"; // Specify the route for Course A
      case "DASHBOARD":
        return ""; // Specify the route for Course B
      case "Course C":
        return "/course-c"; // Specify the route for Course C
      // Add more cases as needed for different courses
      default:
        return "/not-found"; // Redirect to a not found page or a default route
    }
  };




  return (
    <SidebarContext.Provider value={{ isSidebarOpen, toggleSidenav }}>
      <div>
        <style>
          {`
      .hamburgers {
  width: 40px; /* Adjust the width as needed */
  height: 30px; /* Adjust the height as needed */
  display: flex;
  flex-direction: column;
  justify-content: space-between; /* Space the lines evenly */
  cursor: pointer; /* Change cursor on hover */
  background:#2a2a2a;
}

.line {
  height: 3px; /* Thickness of the lines */
  margin-bottom:7px;
  width: 86%; /* Full width of the hamburger */
  background-color: #fff; /* Color of the lines */
  transition: background-color 0.3s ease; /* Smooth transition for hover effects */
}

/* Optional: Add hover effects */
.hamburger:hover .line {
  background-color: #ccc; /* Change color on hover */
}
  
           
        `}
        </style>

        <div className={`sidenav ${isSidebarOpen ? 'open' : 'closed'}`}>
          <div className={`nav-control`} onClick={toggleSidenav}>
            {/* Apply dynamic class based on isSidebarOpen */}
            <div className={`hamburgers ${isSidebarOpen ? 'active' : ''}`}>

            </div>
          </div>
          <div className={`deznav ${isHidden ? 'hidden' : ''}`}>
            <div className="deznav-scroll">
              <ul className="metismenu" id="menu">
                <li className="nav-label first">Main Menu</li>
                <li><Link to="/cntrollers-table-data" style={{
                  textAlign: 'center',
                  cursor: 'pointer',
                  color: '#fff',
                  textDecoration: 'none', // No underline on the link
                }}>Controllers</Link></li>

<li><Link to="/cntrollers-table-data" style={{
                  textAlign: 'center',
                  cursor: 'pointer',
                  color: '#fff',
                  textDecoration: 'none', // No underline on the link
                }}>JAVA</Link></li>
                <li>

                <li><Link to="/cntrollers-table-data" style={{
                  textAlign: 'center',
                  cursor: 'pointer',
                  color: '#fff',
                  textDecoration: 'none', // No underline on the link
                }}>Flutter</Link></li>
                <li></li>

                  {/* <button onClick={() => setIsModalOpen(true)} className="btn btn-primary">Add</button> */}
                </li>
              </ul>
              <div>

                {loading ? (
                  <p>Loading...</p> // Show loading message while fetching data
                ) : (
                  <div>
                    {apiData.length > 0 ? (
                      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', position: 'relative', marginTop: '20px', marginLeft: '27px' }}>
                        {/* Course Items */}
                        {apiData.map((course) => (
                          <div key={course.navId} style={{ display: 'flex', alignItems: 'center', marginRight: '30px', marginBottom: '30px', position: 'relative' }}>
                            {/* Course Item */}
                            <div
                              className="course-item"
                              onClick={() => handleCourseClick(course)} // Open sidebar on click
                              style={{
                                textAlign: 'center',
                                cursor: 'pointer',
                                color: '#fff',
                              }}
                              >
                              <Link
                                to={getCourseRoute(course.name)} // Use a function to get the route based on the course name
                                style={{
                                  textAlign: 'center',
                                  cursor: 'pointer',
                                  color: '#fff',
                                  textDecoration: 'none', // No underline on the link
                                }}
                              >
                                {/* Course image */}
                                {course.image ? (
                                  <img
                                    src={`data:image/png;base64,${course.image}`}
                                    alt={course.name}
                                    style={{ width: '35px', height: '35px' }}
                                  />
                                ) : null}
                                {/* Course name */}
                                <strong style={{ display: 'block', marginTop: '5px' }}>{course.name}</strong>
                              </Link>
                            </div>

                            {/* Sidebar next to the clicked course */}
                            {selectedCourseId === course.navId && (
                              <div
                                className="sidenav"
                                style={{
                                  position: 'absolute',
                                  left: '100%',
                                  marginLeft: '20px',
                                  width: '165px',
                                  height: '130px',
                                  transition: '0.3s',
                                  backgroundColor: '#2a2a2a',
                                  padding: '10px',
                                  marginTop: '-50px',
                                  overflowX: 'clip',
                                  color: '#fff',
                                  zIndex: 1,
                                }}
                              >
                                <ul style={{ listStyle: 'none', padding: '0', color: '#fff', textAlign: 'center' }}>
                                  <button onClick={() => { setIsSecondModalOpen(true); setNavId(course.navId); }} className="btn btn-primary">
                                    Add
                                  </button>
                                  {subNavData.map((subNavItem) => {
                                    // Dynamic route logic
                                    const dynamicRoute = (() => {
                                      switch (subNavItem.name) {
                                        case "add-courses":
                                          return "/add-courses";
                                        case "test1":
                                          return "/test1"; // Specify the route for "test1" 
                                        case "Students":
                                          return "/Students"; // Specify the route for "Students"
                                        case "All Courses":
                                          return "/All Courses"; // Specify the route for "Students" 
                                        case "Add Courses":
                                          return "/Add Courses"; // Specify the route for "Students" 
                                        case "Enroll":
                                          return "/Enroll"; // Specify the route for "Students"  Controllers
                                        case "All Students":
                                          return "/All Students"; // Specify the route for "Students" 
                                        case "Controllers":
                                          return "/Controllers"; // Specify the route for "Students" 
                                        default:
                                          return `/courses/${subNavItem.name.toLowerCase().replace(/\s+/g, '-')}`; // Default route mapping
                                      }
                                    })();

                                    return (
                                      <li key={subNavItem.subNavId}>
                                        <Link
                                          to={dynamicRoute} // Use dynamicRoute to navigate to different pages
                                          style={{ color: '#fff', textDecoration: 'none' }} // Optional styling for Link
                                        >
                                          {subNavItem.name}
                                        </Link>
                                      </li>
                                    );
                                  })}
                                </ul>
                              </div>
                            )}
                          </div>
                        ))}
                      </div>
                    ) : (
                      <p>No data available.</p>
                    )}
                  </div>
                )}
              </div>

            </div>

          </div>






        </div>

      </div>
      {/* Second Modal */}

      {/* Second Modal */}
      {isSecondModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button className="close-button" onClick={() => setIsSecondModalOpen(false)}>
              &times;
            </button>
            <form onSubmit={handleSubmitss}>
              <div className="form-group">
                <label className="form-label">Banner Image Title 1</label>
                <input
                  type="text"
                  className="form-control"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </div>
              <div>
                <button type="submit" className="btn btn-primary">Submit</button>
              </div>
            </form>
          </div>
        </div>
      )}


      {/* Modal for adding an image */}
      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button className="close-button" onClick={() => setIsModalOpen(false)}>
              &times;
            </button>
            <form onSubmit={handleSubmits}>
              <div className="col-lg-2 col-md-12 col-sm-12">
                <div className="add-choosen">
                  <div className="input-blocks">
                    <div className="image-upload">
                      <input
                        type="file"
                        id="imageInput"
                        accept="image/*"
                        onChange={handleImageChange}
                        style={{ display: 'none' }}
                        required
                      />
                      <label htmlFor="imageInput" className="image-uploads">
                        <i data-feather="plus-circle" className="plus-down-add me-0"></i>
                        <h4>Add Image</h4>
                        {posterImage && (
                          <img
                            src={posterImage}
                            alt="Preview"
                          />
                        )}
                      </label>
                    </div>
                  </div>
                </div>
              </div>
              <div className="form-group">
                <label className="form-label">Banner Image Title 1</label>
                <input
                  type="text"
                  className="form-control"
                  value={name}
                  onChange={(e) => setName(e.target.value)} // Update the name state
                  required
                />
              </div>
              <div>
                <button type="submit" className="btn btn-primary">Submit</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </SidebarContext.Provider>

  );
};

export default Sidenav;
