import React from 'react';
import { Bar, Doughnut, Line } from 'react-chartjs-2'; // Import chart types
import 'chart.js/auto'; // Automatically register all components

const Charts = () => {
  // Sample data for the charts
  const barData = {
    labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
    datasets: [
      {
        label: 'University Survey',
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
        data: [65, 59, 80, 81, 56, 55, 40],
      },
    ],
  };

  const doughnutData = {
    labels: ['Red', 'Blue', 'Yellow'],
    datasets: [
      {
        label: 'Donut Chart',
        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
        data: [300, 50, 100],
      },
    ],
  };

  const lineData = {
    labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
    datasets: [
      {
        label: 'University Survey Area',
        fill: true,
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        borderColor: 'rgba(75, 192, 192, 1)',
        data: [65, 59, 80, 81, 56, 55, 40],
      },
    ],
  };

  return (
    <div className="content-body">
      <div className="container-fluid">
        <div className="row">
          <div className="col-xl-6 col-xxl-6 col-lg-12 col-sm-12">
            <div className="card">
              <div className="card-header">
                <h3 className="card-title">University Survey</h3>
              </div>
              <div className="card-body">
                <div className="morris_chart_height" style={{ height: '300px' }}>
                  <Bar data={barData} />
                </div>
              </div>
            </div>
          </div>
          <div className="col-xl-3 col-xxl-3 col-sm-6">
            <div className="card">
              <div className="card-header">
                <h3 className="card-title">Doughnut Chart</h3>
              </div>
              <div className="card-body">
                <div className="morris_chart_height" style={{ height: '300px' }}>
                  <Doughnut data={doughnutData} />
                </div>
              </div>
            </div>
          </div>
          <div className="col-xl-3 col-xxl-3 col-sm-6">
            <div className="card">
              <div className="card-header">
                <h3 className="card-title">University Survey Area</h3>
              </div>
              <div className="card-body">
                <div className="morris_chart_height" style={{ height: '300px' }}>
                  <Line data={lineData} />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Charts;
