import ToolAndfixtureBanner from './ToolAndfixtureBanner';
import ToolandFixtureTabs from './ToolandFixtureTabs';
import { Link } from "react-router-dom";
import React, { useState } from "react";
import Carousel from "react-bootstrap/Carousel";
import toolfix from "../images/banners/Tool-&-Fixture-v2.jpg";

const ToolAndFixtureDesign = () => {
  { window.scrollTo({ top: 0, behavior: 'smooth' }); };
  
  const [activeTab, setActiveTab] = useState('tab1');
 
  const tabs = [
    {
        id: 'tab1',
        label: 'Level 1 Certification',
        content: (
            <>
                <div className="row">

                    <div className="col-lg-6"> {/* Adds left offset */}
                        {/* <h2 style={{color: "#000",}}> And Fixture Design:-</h2> */}
                        
                        <h2 style={{ color: "rgb(224 120 44)", }}>Certified Tool  <span style={{ color: "#fff" }}>And Fixture Design </span></h2>  
                        <div  style={{ height: '100%', width:"10%",marginTop:"-150px", marginLeft:"109px" }}>
                        <Link to="/enroll">
                            <button class="btn btn-primary" style={{ marginTop: '109px', width: "138%", background: "#e0782c",marginLeft:"380px" }}>
                                <span>ENROLL</span>
                            </button>
                        </Link>
                    </div>
                        <ul style={{
                            color: "#fff",
                            fontSize: "20px",
                            textAlign: "justify"
                        }}>
                            <li>Explore the essential principles of designing and fabricating tools and fixtures to optimize manufacturing processes.</li>
                            <li>Learn to choose materials based on their characteristics to optimize performance, durability, and cost.</li>

                        </ul>

                    </div>
                  



                </div>
                <div className="row">
                    <div className=""> {/* Adds left offset */}
                        <p style={{
                            color: "#fff",
                            fontSize: "20px",
                            textAlign: "justify",
                            marginTop:"35px",
                        }}>Welcome to the Tool and Fixture Design course at IIA. This comprehensive program is designed to equip students with the knowledge and skills required to design efficient and effective tools and fixtures for various manufacturing processes. From concept to implementation, this course covers the entire spectrum of tool and fixture design principles.Tool and fixture design stands as a cornerstone in manufacturing, ensuring, efficiency, precision and consistency in production processes. These designs, meticulously crafted by engineers, provide the foundation for the creation of various components and products. Precision in tool and fixture design is paramount, as it directly impacts the quality and uniformity of the end products.</p>


                    </div>
                </div>

            </>
        )
    },
    // {
    //     id: 'tab2',
    //     label: 'Level 2 Certification',
    //     content: (
    //         <>
    //             <div className="row">


    //                 <div className="col-lg-6"> 
    //                     <h2 style={{ color: "rgb(224 120 44)", }}>Advance Tool   <span style={{ color: "#fff" }}>And Fixture Design </span></h2>
    //                     <div  style={{ height: '100%', width:"10%",marginTop:"-150px", marginLeft:"109px" }}>
    //                     <Link to="/enroll">
    //                         <button class="btn btn-primary" style={{ marginTop: '109px', width: "138%", background: "#e0782c",marginLeft:"380px" }}>
    //                             <span>ENROLL</span>
    //                         </button>
    //                     </Link>
    //                 </div>
    //                     <ul style={{
    //                         color: "#fff",
    //                         fontSize: "20px",
    //                         textAlign: "justify"
    //                     }}>
    //                         <li>Explore the essential principles of designing and fabricating tools and fixtures to optimize manufacturing processes.</li>
    //                         <li>Learn to choose materials based on their characteristics to optimize performance, durability, and cost.</li>
                            
    //                     </ul>

    //                 </div>
                  
    //             </div>
    //             <div className="row">
    //                 <div className=""> {/* Adds left offset */}
    //                     <p style={{
    //                         color: "#fff",
    //                         fontSize: "20px",
    //                         textAlign: "justify"
    //                     }}>Welcome to the Tool and Fixture Design course at IIA. This comprehensive program is designed to equip students with the knowledge and skills required to design efficient and effective tools and fixtures for various manufacturing processes. From concept to implementation, this course covers the entire spectrum of tool and fixture design principles.Engineers meticulously analyze factors like material properties, forces involved and manufacturing constraints to create designs that optimize production speed, accuracy and safety. Through careful consideration of these elements, tool and fixture designs streamline manufacturing operations, reduce errors and enhance overall productivity.</p>



    //                 </div>
    //             </div>
    //         </>
    //     )
    // },
];
  return (
    <div>
    <section style={{ marginTop: "-40px" }}>
      <Carousel className="res-nav-mob no-indicators" interval={300000} controls={false}>
        <img className="d-block w-100" src={toolfix} alt="Tool & Fixture Design" style={{ height: "650px", width: "90%", }} />
        <Carousel.Caption style={{ width: "100%", textAlign: "left" }}>
          <div className="row">

            <div className="col-lg-4 dtd" style={{}}>
              <div className="" style={{ width: "100%", marginBottom: "100px" }}>
                <h2 className="banner-content" style={{ fontFamily: "Poppins, SansSerif", fontSize: "32px", fontWeight: "600", width: "127%", color: "#fff" }}>
                  Unlock your potential in manufacturing "From Concept to Creation"
                </h2>
                <h2 className="banner-content" style={{ fontSize: "40px", color: "#fff", fontFamily: "Roboto", fontWeight: "800", width: "110%" }}>
                  TOOL & FIXTURE <span style={{ color: "#F58842" }}>DESIGN</span>
                </h2>

                <p className="banner-contentul" style={{ fontSize: "17px", color: "#fff", textAlign: "justify", textIndent: "50px", fontWeight: "400", fontWeight: "bold" }}>
                  1. Industrial Oriented Training (IOT)
                </p>
                <p className="banner-contentul" style={{ fontSize: "17px", color: "#fff", textAlign: "justify", textIndent: "50px", fontWeight: "400", fontWeight: "bold", }}>
                  2. Job Oriented Skill Enhancement (JOSE)
                </p>
                <p className="banner-contentul" style={{ fontSize: "17px", color: "#fff", textAlign: "justify", textIndent: "50px", fontWeight: "400", fontWeight: "bold", }}>
                  3. 100% Placement Support
                </p>
                <p className="banner-contentul" style={{ fontSize: "25px", color: "#fff", textAlign: "justify", fontWeight: "400", marginLeft: "49px", fontWeight: "bold", }}>
                  NX & CATIA Software free training
                </p>

              </div>
            </div>


            <div className="col-lg-8 dtdt ">
              <h2 className="banner-content" style={{ fontFamily: "Poppins, SansSerif", fontSize: "27px", fontWeight: "600", marginTop: "-153px ", color: "#fff" }}>
                “Only Institute Automating Careers Across India"
              </h2>
            </div>
            <div style={{}}>



            </div>
          </div>






        </Carousel.Caption>
      </Carousel>
    </section>
  

  
    <div className="tab-wrapper" style={{ padding: '30px 69px', backgroundColor: "#fff", marginLeft: "139px" }}> {/* Add padding here  data fetching */}
        <div className="tab-container">
            {tabs.map(tab => (
                <button
                    key={tab.id}
                    className={`tab-button ${activeTab === tab.id ? 'active' : ''}`}
                    onClick={() => setActiveTab(tab.id)}
                >
                    {tab.label}
                </button>
            ))}
        </div>
        <div className="tab-content">
            <p>{currentTab.content}</p>
        </div>
        <style>
            {`
      .tab-container {
        display: flex;
        justify-content: center; /* Center the tabs horizontally */
        margin-bottom: 20px; /* Space between tabs and content */
      }
      .tab-button {
        flex: 1; /* Make all tabs take equal width */
        max-width: 500px; /* Set a maximum width for each tab */
        padding: 10px;
        border: none;
        background: none;
        cursor: pointer;
        font-size: 16px;
        transition: background-color 0.3s;
        margin-right: 5px; /* Add some space between tabs */
        outline: none; /* Remove default outline */
       
        // border-radius: 50px;
            background: #4f4e70;
             color: #f58842; /* Default text color */
                margin-top: 10px;
                font-size: 18px;
      }
      .tab-button:hover {
        background-color: #f0f0f0;
      }
      .tab-button.active {
        border-bottom: 2px solid #524DA1; /* Active tab color */
        font-weight: bold;
        color: #f58842; /* Change text color for active tab */
            // border-radius: 50px;
            background: #efefef;
                margin-top: 10px;
                font-size: 18px;
      }
    
      .tab-content {
