  const [subNavData, setSubNavData] = useState([]); // State to hold sub-navigation data
  const [loading, setLoading] = useState(true); // State to manage loading status
  const [error, setError] = useState(null); // State for error handling
  const [name, setName] = useState('');
  const [isSecondModalOpen, setIsSecondModalOpen] = useState(false); // Second modal state
  const [user, setUser] = useState('');
  const location = useLocation(); 
  const [filter, setFilter] = useState('');
  const [selected, setSelected] = useState([]); 
  const [page, setPage] = useState(0); 
  const [rowsPerPage, setRowsPerPage] = useState(5);
  const { navId: routeNavId } = useParams(); // Get navId from route parameters
  const [navId, setNavId] = useState(() => (routeNavId ? Number(routeNavId) : undefined)); // Initialize navId with routeNavId
  const [isSelected, setIsSelected] = useState(false); 


  const datasource = [
    {
      jobTitleDesignation: 'Junior_Engineer',
      candidateId: 1,
      candidateName: 'Kumar Ravi',
      contactNumber: '9876543210',
      emailId: 'rcreddy2222@gmail.com',
      serviceCommitment: '3',
      serviceBreakAmount: '200,000',
      ctc: '240,000',
      candidatesStatus: 'Pending',
    },
    {
      jobTitleDesignation: 'Junior_Executive',
      candidateId: 2,
      candidateName: 'Ravi',
      contactNumber: '8927287280',
      emailId: 'ravi@gmail.com',
      serviceCommitment: '2',
      serviceBreakAmount: '12,000',
      ctc: '3',
      candidatesStatus: 'Pending',
    },
    {
      jobTitleDesignation: 'Junior_Engineer',
      candidateId: 3,
      candidateName: 'Gaurav',
      contactNumber: '8936738781',
      emailId: 'garyav@gmail.com',
      serviceCommitment: '2',
      serviceBreakAmount: '1,234',
      ctc: '2',
      candidatesStatus: 'Pending',
    },
  ];

  useEffect(() => {
    // Update navId whenever routeNavId changes
    if (routeNavId) {
      setNavId(Number(routeNavId));
    }
  }, [routeNavId]); // Runs when routeNavId changes

  useEffect(() => {
    const fetchSubNavData = async () => {
      if (!navId) {
        console.error('navId is not set');
        setLoading(false);
        return; // Exit if navId is not defined
      }

      try {
        const response = await fetch(`http://10.10.20.9:7071/IIA-Website-1.0.0/subsidenavbar/subnav/${navId}`);
        console.log("Response status:", response.status); // Log response status

        if (!response.ok) {
          console.error('Response not OK:', response);
          throw new Error('Failed to fetch sub-navigation data: ' + response.statusText);
        }

        const data = await response.json();
        console.log('Fetched sub-navigation data:', data); // Log fetched data for debugging

        // Check if the response structure is as expected
        if (Array.isArray(data)) {
          setSubNavData(data.length > 0 ? data : []); // If it's an array, set it directly
        } else if (data) {
          setSubNavData([data]); // If it's a single object, wrap it in an array
        } else {
          console.log('No valid data returned from API');
          setSubNavData([]); // Reset state if no valid data
        }
      } catch (error) {
        console.error('Error fetching sub-navigation data:', error);
        setError(error.message); // Set error message to state
      } finally {
        setLoading(false); // Stop loading regardless of success or failure
      }
    };

    fetchSubNavData(); // Call the fetch function whenever navId changes
  }, [navId]); // Fetch data whenever navId changes


  // Define a mapping between subNav names and routes
  // useEffect(() => {
  //   // Fetch your subNavData (dynamic data)
  //   // Example fetch call:
  //   setSubNavData([
  //     { subNavId: 1, name: "OnBoarding" },
  //     { subNavId: 2, name: "Profile" },
  //     { subNavId: 3, name: "Hr Executive" },
  //     { subNavId: 4, name: "Hr Manager" }, // Ensure this matches
  //     { subNavId: 5, name: "Summary" }, // Ensure this matches 
     
  //     // Add more dynamic data if necessary
  //   ]);
  // }, []);


  


  const handleFilterChange = (event) => {
    setFilter(event.target.value);
  };

  const handleSelectAllClick = (event) => {
    if (event.target.checked) {
      const newSelected = datasource.map((row) => row.candidateId);
      setSelected(newSelected);
    } else {
      setSelected([]);
    }
  };

  const handleClick = (event, row) => {
    const selectedIndex = selected.indexOf(row.candidateId);
    let newSelected = [];

    if (selectedIndex === -1) {
      newSelected = newSelected.concat(selected, row.candidateId);
    } else if (selectedIndex === 0) {
      newSelected = newSelected.concat(selected.slice(1));
    } else if (selectedIndex === selected.length - 1) {
      newSelected = newSelected.concat(selected.slice(0, -1));
    } else if (selectedIndex > 0) {
      newSelected = newSelected.concat(
        selected.slice(0, selectedIndex),
        selected.slice(selectedIndex + 1),
      );
    }

    setSelected(newSelected);
  };

  const handlePageChange = (event, newPage) => {
    setPage(newPage);
  };

  const handleRowsPerPageChange = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

 




  const handleSubmitss = async (e) => {
    e.preventDefault();

    // Prepare the data to be sent to the API
    const requestBody = {
      navId, // Use the navId from the selected course
      name,
    };

    console.log('Request Body:', requestBody); // Log the request body

    // POST API call
    try {
      const response = await fetch('http://10.10.20.9:7071/IIA-Website-1.0.0/subsidenavbar/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });

      if (!response.ok) {
        const errorData = await response.json(); // Log error response
        console.error('Error response from server:', errorData);
        throw new Error('Failed to submit the form: ' + errorData.message);
      }

      const data = await response.json();
      alert('Form submitted successfully')
      console.log('Form submitted successfully:', data);

      // Optionally, you might want to fetch the updated data here
      // await fetchData(); // Assuming fetchData is a function to fetch data

      // Handle the success, close modal, or reset form
      setIsSecondModalOpen(false);
      setName('');
    } catch (error) {
      console.error('Error submitting the form:', error);
    }
  };
  

  return (
    <div
      className="content-body"
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'row', // This will make the children align horizontally
        flexWrap: 'wrap', // Allows wrapping to next line if the width is exceeded
        marginTop: '90px',
        backgroundColor: '#f8f9fa', // Light background color for the navbar
        padding: '10px 20px', // Padding for the navbar
        boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)', // Subtle shadow for depth
        borderRadius: '5px', // Optional: rounded corners
      }}
    >

      <button onClick={() => { setIsSecondModalOpen(true); setNavId(user.navId); }} className="btn btn-primary">
        Add
      </button>
      {loading && <p>Fetching data...</p>} {/* Show fetching message while loading */}
      {error && <p>Error: {error}</p>} {/* Show error message if there's an error */}
      {!loading && !error && subNavData.length === 0 && <p>No data available.</p>} {/* Message if no data */}
      {!loading && !error && subNavData.length > 0 && (
        <div style={{ display: 'flex', gap: '20px' }}>
          {subNavData.map((subNav) => {
            // Dynamic route logic
            const dynamicRoute = (() => {
              switch (subNav.name) {
                case "Profile":
                  return "/Profile/28";
                case "test1":
                  return "/test1"; // Specify the route for "test1"
                case "Students":
                  return "/Students"; // Specify the route for "test1" 
                case "All Courses":
                  return "/All Courses"; // Specify the route for "test1"  
                case "Enroll":
                  return "/Enroll"; // Specify the route for "test1"
                case "All Students":
                  return "/All Students"; // Specify the route for "test1"
                case "Add Courses":
                  return "/Add Courses"; // Specify the route for "test1"
                default:
                  return ``; // Default route mapping
              }
            })();


            const isActive = location.pathname === dynamicRoute;

            return (
                <Link
                key={subNav.subNavId}
                to={dynamicRoute}
                style={{
                  color: isActive ? '#fff' : '#000', // Text color remains unchanged
                  backgroundColor: subNav.name === 'OnBoarding'
                    ? (isActive ? '#007bff' : '#007bff')  // Blue background for 'OnBoarding' whether active or not
                    : (isActive ? '#007bff' : 'transparent'),  // Active links have blue, others are transparent
                  textDecoration: 'none',
                  padding: '10px 15px',

                  borderRadius: '4px',
                  transition: 'background-color 0.3s, color 0.3s', // Smooth transition for background color
                }}
              >
                {subNav.name}
              </Link>
              
            
            
            );
          })}
        </div>
      )}


(
    <Card>
      <CardHeader
        style={{ backgroundColor: 'whitesmoke', marginTop: '2%' }}
        title={
          <TextField
            placeholder="Filter"
            value={filter}
            onChange={handleFilterChange}
            className="filterAlignment"
          />
        }
        action={
          <>
            <input
              type="file"
              accept=".csv, application/vnd.openxmlformats-officedocument.spreadsheetml.sheet, application/vnd.ms-excel"
              style={{ display: 'none' }}
              id="fileUpload"
            />
            <label htmlFor="fileUpload">
              <Button variant="extendedFab" component="span">
                <CloudDownload />
                Import
              </Button>
            </label>
            <Button variant="extendedFab" onClick={() => console.log('Export')}>
              <CloudUpload />
              Export
            </Button>
            <Button variant="extendedFab" onClick={() => console.log('Add')}>
              +Add
            </Button>
          </>
        }
      />
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell padding="checkbox">
                <Checkbox
                  indeterminate={selected.length > 0 && selected.length < datasource.length}
                  checked={datasource.length > 0 && selected.length === datasource.length}
                  onChange={handleSelectAllClick}
                />
              </TableCell>
              <TableCell>S.No</TableCell>
              <TableCell>Job Title/Designation</TableCell>
              <TableCell>Candidate Id</TableCell>
              <TableCell>Candidate Name</TableCell>
              <TableCell>Contact Number</TableCell>
              <TableCell>Email Id</TableCell>
              <TableCell>Service Commitment (Years)</TableCell>
              <TableCell>Service Break Amount</TableCell>
              <TableCell>CTC (INR)</TableCell>
              <TableCell>Status</TableCell>
              <TableCell>Action</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {datasource
              .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
              .map((row, index) => {
              
                return (
                  <TableRow
                    hover
                    onClick={(event) => handleClick(event, row)}
                    role="checkbox"
                
                    tabIndex={-1}
                    key={row.candidateId}
                   
                  >
                    <TableCell padding="checkbox">
                      <Checkbox />
                    </TableCell>
                    <TableCell>{index + 1}</TableCell>
                    <TableCell>{row.jobTitleDesignation.replaceAll('_', ' ')}</TableCell>
                    <TableCell>{row.candidateId}</TableCell>
                    <TableCell>{row.candidateName}</TableCell>
                    <TableCell>{row.contactNumber}</TableCell>
                    <TableCell>{row.emailId}</TableCell>
                    <TableCell>{row.serviceCommitment}</TableCell>
                    <TableCell>{row.serviceBreakAmount}</TableCell>
                    <TableCell>{row.ctc}</TableCell>
                    <TableCell>{row.candidatesStatus}</TableCell>
                    <TableCell>
                      <IconButton onClick={() => console.log('View', row.candidateId)}>
                        <RemoveRedEye />
                      </IconButton>
                      <IconButton onClick={() => console.log('Edit', row.candidateId)}>
                        <Edit />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                );
              })}
          </TableBody>
        </Table>
      </TableContainer>
      <CardActions>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25, 100]}
          component="div"
          count={datasource.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handlePageChange}
          onRowsPerPageChange={handleRowsPerPageChange}
        />
      </CardActions>
    </Card>
      {isSecondModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button className="close-button" onClick={() => setIsSecondModalOpen(false)}>
              &times;
            </button>
            <form onSubmit={handleSubmitss}>
              <div className="form-group">
                <label className="form-label">Title</label>
                <input
                  type="text"
                  className="form-control"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </div>
              <div>
                <button type="submit" className="btn btn-primary">Submit</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>

  );
};

export default OnBoarding;
